/*Table structure for table `t_design_work` */

DROP TABLE IF EXISTS `t_design_work`;

CREATE TABLE `t_design_work` (
  `seq` int(11) NOT NULL AUTO_INCREMENT COMMENT 'seq',
  `view_cnt` int(11) DEFAULT '0' COMMENT '조회수',
  `title` char(200) COLLATE utf8_bin DEFAULT NULL COMMENT '작품명',
  `license` varchar(3) COLLATE utf8_bin DEFAULT NULL COMMENT '라이센스',
  `point` int(11) DEFAULT NULL COMMENT '포인트',
  `thumb_uri` varchar(200) COLLATE utf8_bin DEFAULT NULL COMMENT '썸네일',
  `contents` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '작품설명',
  `register_time` char(12) COLLATE utf8_bin DEFAULT NULL COMMENT '등록일시',
  `tags` varchar(200) COLLATE utf8_bin DEFAULT NULL COMMENT '테그',
  `member_seq` int(11) DEFAULT NULL COMMENT '회원seq',
  `update_time` char(12) COLLATE utf8_bin DEFAULT NULL COMMENT '수정일시',
  `del_flag` char(1) COLLATE utf8_bin DEFAULT 'N' COMMENT '삭제flag(Y,N)',
  PRIMARY KEY (`seq`),
  KEY `member_seq` (`member_seq`)
